CREATE TABLE "{PREFIX}article" ( "article_id" INTEGER NOT NULL PRIMARY KEY, "tree_id" int(11) default '0' , "user_id" int(11) default '0' , "validator_id" int(11) default '0' , "article_date" datetime NOT NULL default '1900-01-01 00:00:00' , "article_status" tinyint(4) NOT NULL default '0' , "article_revisioninfo" varchar(255) NOT NULL default '' , "article_content" mediumtext NOT NULL );
